package com.example.veterinarioapp.data

import android.app.Application
import androidx.room.Room
import com.example.veterinarioapp.Entity.ReviewEntity
import kotlinx.coroutines.DelicateCoroutinesApi
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.GlobalScope
import kotlinx.coroutines.launch

class Aplicacion : Application() {
    companion object {
        lateinit var baseDeDatos: BaseDeDatos
    }

    override fun onCreate() {
        super.onCreate()


        baseDeDatos = Room.databaseBuilder(
            this,
            BaseDeDatos::class.java,
            "BaseDeDatos"
        )
            .fallbackToDestructiveMigration()
            .build()

        agregarReviews()
    }

    @OptIn(DelicateCoroutinesApi::class)
    private fun agregarReviews() {
        // Se usa GlobalScope porque esta operación de inserción inicial de datos
        // no está ligada a ningún componente específico de la UI y debe ejecutarse
        // independientemente del ciclo de vida de una Activity.
        // Usar lifecycleScope aquí podría hacer que la operación se cancele si
        // la Activity se destruye antes de completarse.
        GlobalScope.launch(Dispatchers.IO) {
            baseDeDatos.reviewDao().borrarTodasLasReviews()
            baseDeDatos.reviewDao().actualizarReviews()

                listOf(
                    ReviewEntity(
                        titulo = "El brote de fiebre aftosa afecta a ganado en varias provincias de España",
                        descripcion = "La fiebre aftosa ha sido detectada en varias explotaciones ganaderas, lo que ha llevado a las autoridades a implementar medidas de control y prevención.",
                        fecha = "2025-03-01",
                        esFavorita = false,
                        imagenUrl = "data:image/jpeg;base64,",
                        reviewUrl = "https://www.woah.org/es/enfermedad/fiebre-aftosa/"
                    ),
                    ReviewEntity(
                        titulo = "Brotes de leptospirosis en perros: lo que debes saber",
                        descripcion = "Se han reportado brotes de leptospirosis en varias áreas, afectando a perros y generando preocupación entre los dueños de mascotas. Se recomienda la vacunación.",
                        fecha = "2025-03-02",
                        esFavorita = false,
                        imagenUrl = "data:image/jpeg;base64,",
                        reviewUrl = "https://cuasveterinaria.es/blog/tratamiento-leptospirosis-en-perros/"
                    ),
                    ReviewEntity(
                        titulo = "El síndrome de la lengua azul se expande entre los rebaños de ovejas",
                        descripcion = "Este síndrome, transmitido por mosquitos, está afectando a los rebaños de ovejas, causando preocupaciones entre los ganaderos.",
                        fecha = "2025-03-01",
                        esFavorita = false,
                        imagenUrl = "data:image/jpeg;base64,",
                        reviewUrl = "https://www.csic.es/es/actualidad-del-csic/el-virus-de-la-lengua-azul-evade-la-respuesta-inmune-del-hospedador-para-desencadenar-la-enfermedad"
                    ),
                    ReviewEntity(
                        titulo = "Investigaciones sobre el virus del moquillo canino en refugios de animales",
                        descripcion = "Se han reportado brotes de moquillo canino en varios refugios, lo que ha llevado a las autoridades veterinarias a intensificar las campañas de vacunación.",
                        fecha = "2025-02-28",
                        esFavorita = false,
                        imagenUrl = "https://www.zooplus.es/magazine/wp-content/uploads/2020/07/moquillo-canino.jpeg",
                        reviewUrl = "https://cuasveterinaria.es/blog/tratamiento-moquillo-perros/"
                    ),
                    ReviewEntity(
                        titulo = "El impacto del parvovirus en cachorros: un llamado a la vacunación",
                        descripcion = "El parvovirus canino sigue siendo una amenaza para los cachorros, destacando la importancia de la vacunación en las primeras etapas de vida.",
                        fecha = "2025-02-26",
                        esFavorita = false,
                        imagenUrl = "https://www.mascotasanasydivertidas.com/wp-content/uploads/sites/12/2022/07/Nobivac-Puppy_Breeder_B5A4542-cropped.png",
                        reviewUrl = "https://www.mascotasanasydivertidas.com/la-parvovirosis-en-perros-y-la-importancia-de-la-vacunacion-en-cachorros/"
                    ),
                    ReviewEntity(
                        titulo = "Prevención de la enfermedad de Lyme en perros: consejos para propietarios",
                        descripcion = "La enfermedad de Lyme, transmitida por garrapatas, está en aumento. Este artículo ofrece recomendaciones para prevenir infecciones en perros.",
                        fecha = "2025-03-04",
                        esFavorita = false,
                        imagenUrl = "https://www.tiendanimal.es/articulos/wp-content/uploads/2024/04/tratamiento-como-prevenir-enfermedad-lyme.jpg",
                        reviewUrl = "https://www.santevet.es/articulo/enfermedad-de-lyme-en-perros"
                    ),
                    ReviewEntity(
                        titulo = "La importancia de la desparasitación en mascotas: un enfoque veterinario",
                        descripcion = "Expertos veterinarios destacan la importancia de la desparasitación regular en gatos y perros para mantener su salud y bienestar.",
                        fecha = "2025-02-27",
                        esFavorita = false,
                        imagenUrl = "https://k9rescate.com/wp-content/uploads/2023/11/importanci_desparasitacion.jpg",
                        reviewUrl = "https://www.deltavet.es/l/la-importancia-de-la-desparasitacion-de-tu-mascota/"
                    ),
                    ReviewEntity(
                        titulo = "Aumento de casos de dermatitis atópica en perros: causas y tratamientos",
                        descripcion = "Veterinarios informan sobre el aumento de dermatitis atópica en perros, analizando sus causas y opciones de tratamiento disponibles.",
                        fecha = "2025-02-25",
                        esFavorita = false,
                        imagenUrl = "data:image/jpeg;base64,",
                        reviewUrl = "https://veterinarios.hospitalveterinariopuchol.com/blog/dermatitis-atopica-canina/"
                    ),
                    ReviewEntity(
                        titulo = "Los parásitos intestinales afectan a la salud de los gatos en refugios",
                        descripcion = "Los refugios de gatos están lidiando con un aumento en los casos de infecciones por parásitos intestinales, lo que resalta la necesidad de chequeos veterinarios regulares.",
                        fecha = "2025-03-06",
                        esFavorita = false,
                        imagenUrl = "data:image/jpeg;base64,",
                        reviewUrl = "https://www2.zoetis.es/productos-y-soluciones/gatos/parasitos-internos-en-gatos"
                    ),
                    ReviewEntity(
                        titulo = "El síndrome de Cushing en gatos: diagnóstico y tratamiento",
                        descripcion = "Este artículo explora el síndrome de Cushing en gatos, incluyendo síntomas, diagnóstico y opciones de tratamiento disponibles.",
                        fecha = "2024-02-15",
                        esFavorita = false,
                        imagenUrl = "data:image/jpeg;base64,",
                        reviewUrl = "https://www.zooplus.es/magazine/gatos/salud-del-gato-y-cuidados/cushing-en-gatos"
                    )
                ).forEach { review ->
                    baseDeDatos
                        .reviewDao()
                        .agregarReview(review)
                }
            }
        }
    }

